{
  "main": {
    "name": "PersonSerializerByYAML",
    "fields": [
      {
        "name": "first_name",
        "field": "CharField"
      },
      {
        "name": "last_name",
        "field": "CharField"
      },
      {
        "name": "age",
        "field": "IntegerField"
      },
      {
        "name": "email",
        "field": "EmailField"
      },
      {
        "name": "sex",
        "field": "definable_serializer.extra_fields.RadioField",
        "field_args": [
          [
            [
              "male",
              "Male"
            ],
            [
              "other",
              "Other"
            ],
            [
              "female",
              "Female"
            ]
          ]
        ],
        "field_kwargs": {
          "allow_blank": false,
          "allow_null": false,
          "initial": "other",
          "inline": true
        }
      },
      {
        "name": "type",
        "field": "definable_serializer.extra_fields.ChoiceRequiredField",
        "field_args": [
          [
            [
              null,
              "--------------"
            ],
            [
              "family",
              "Family"
            ],
            [
              "friend",
              "Friend"
            ],
            [
              "other",
              "Other"
            ]
          ]
        ],
        "field_kwargs": {
          "allow_blank": false,
          "allow_null": false
        }
      },
      {
        "name": "memo",
        "field": "CharField",
        "field_kwargs": {
          "required": false,
          "max_length": 100,
          "style": {
            "base_template": "textarea.html",
            "rows": 5,
            "placeholder": "Hello everyone!"
          }
        }
      }
    ]
  }
}

